step.datasource:
BEGIN TRANSACTION;
WITH CTE AS( SELECT serienummer, Status_ProdGer, Imp_attempts, ROW_NUMBER() OVER(PARTITION BY serienummer ORDER BY serienummer) AS RowNum FROM [ProdGlobe_live].._AB_GerProd_proces WHERE serienummer IS NOT NULL AND status_Prodger != '3' ), Duplicates AS ( SELECT serienummer FROM CTE GROUP BY serienummer HAVING COUNT(*) > 1 ) UPDATE CTE SET Status_ProdGer = 0, Imp_attempts = 0 WHERE RowNum = 1 AND serienummer IN (SELECT serienummer FROM Duplicates);
WITH CTE AS( SELECT serienummer, Status_ProdGer, Imp_attempts, ROW_NUMBER() OVER(PARTITION BY serienummer ORDER BY serienummer) AS RowNum FROM [ProdGlobe_live].._AB_GerProd_proces WHERE serienummer IS NOT NULL AND status_Prodger != '3' ), Duplicates AS ( SELECT serienummer FROM CTE GROUP BY serienummer HAVING COUNT(*) > 1 ) DELETE FROM CTE WHERE RowNum = 2 AND serienummer IN (SELECT serienummer FROM Duplicates);
COMMIT;
